ifMode.script:
jq(function () {
    var encounterDate = '';
    if (typeof encounterDate !== "undefined" && encounterDate !== null &&
        (encounterDate.length > 0)) {
        currentEncounterDate = new Date(+encounterDate);
    }

    setupReturnVisitDateValidation(currentEncounterDate,
                                            "",
                                            ""
                                            );
});
